td.script:
function delete${id}()
{
	var vars = ${vars};
	$.modal.confirm2('Are you sure?', 'Yes', 'No',
		function () {
			CMS.Delete('${bind}','${id}', vars);
		},
		function () {
			CMS.GetBox('${bind}', null, vars);
		},
		'red', 'green'
	);
	return false;
}

