check.label:


	(function(){
		var el = $('#checkbox_${name}');
		var handler = $('<input/>').attr({
			'type': 'checkbox'
		});
		handler.on('change', function () {
			el.val(0);
			if ($(this).is(':checked')) el.val(1);
		});
		handler.insertAfter(el);
		if (1 == el.val()) handler.attr('checked', 'checked');
	}).call();

